import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';
import { IMAGE_BASE_URL } from '../config'; // Import IMAGE_BASE_URL
import api from '../services/api'; // Import the configured API instance

const RecipeDetails = ({ isAdmin }) => {
  const [recipe, setRecipe] = useState(null);
  const { id } = useParams();

  useEffect(() => {
    const fetchRecipe = async () => {
      try {
        const res = await api.get(`/recipes/${id}`);
        setRecipe(res.data);
      } catch (err) {
        console.error('Error fetching recipe:', err);
      }
    };
    fetchRecipe();
  }, [id]);

  if (!recipe) return <div>Loading...</div>;

  return (
    <div className="recipe-details">
      <div className="recipe-info">
        <h1>{recipe.title}</h1>
        <p>זמן הכנה: {recipe.cookingTime} דקות</p>
        <p>מנות: {recipe.servings}</p>
        <h3>מצרכים:</h3>
        <ul>
          {recipe.ingredients.map((ingredient, index) => (
            <li key={index}>{ingredient}</li>
          ))}
        </ul>
        <h3>הוראות הכנה:</h3>
        <ol>
          {recipe.instructions.map((instruction, index) => (
            <li key={index}>{instruction}</li>
          ))}
        </ol>
        {isAdmin && (
          <div>
            <Link to={`/edit-recipe/${recipe._id}`}>
              <button>ערוך מתכון</button>
            </Link>
            {/* Add delete functionality if needed */}
          </div>
        )}
      </div>
      <div className="recipe-image">
        <img 
          src={recipe.image} 
          alt={recipe.title} 
          onError={(e) => {
            e.target.onerror = null; 
            e.target.src = 'https://via.placeholder.com/150?text=No+Image';
          }}
        />
      </div>
    </div>
  );
};

export default RecipeDetails;
